import leetcode.IsSubsequence;
import org.testng.Assert;
import org.testng.annotations.Test;

public class IsSubsequenceTest extends IsSubsequence {

    @Test(description = "IsSubsequenceTests")
    public void isSubsequenceTestCase() {
        String s, t;
        boolean result;
        s = "ace";
        t = "abcde";
        result = isSubsequence(s, t);
        Assert.assertTrue(result, "Actual result for " + s + " and " + t + " = " + result + " != " + true);

        s = "axc";
        t = "ahbgdc";
        result = isSubsequence(s, t);
        Assert.assertFalse(result, "Actual result for " + s + " and " + t + " = " + result + " != " + false);

        s = "";
        t = "";
        result = isSubsequence(s, t);
        Assert.assertTrue(result, "Actual result for " + s + " and " + t + " = " + result + " != " + true);

        s = "ab";
        t = "baab";
        result = isSubsequence(s, t);
        Assert.assertTrue(result, "Actual result for " + s + " and " + t + " = " + result + " != " + true);

        s = "bcd";
        t = "";
        result = isSubsequence(s, t);
        Assert.assertTrue(result, "Actual result for " + s + " and " + t + " = " + result + " != " + true);

        s = "leeeeetcode";
        t = "";
        result = isSubsequence(s, t);
        Assert.assertTrue(result, "Actual result for " + s + " and " + t + " = " + result + " != " + true);

        s = "aaaaaa";
        t = "bbaaaa";
        result = isSubsequence(s, t);
        Assert.assertFalse(result, "Actual result for " + s + " and " + t + " = " + result + " != " + false);
    }
}
